import React, {Component} from "react";
import { StyleSheet, View, TextInput, NativeModules} from "react-native";
import { Card, Text, Button } from '@rneui/base';
import AsyncStorage from "@react-native-async-storage/async-storage";

class Login extends Component {
        constructor(props){
          super(props);
          this.state = {
              username: '',
              password: '',
          };

        }

          doLogin = async (username,password) => {
                if (password=='1234')
                {
                  try {
                    await AsyncStorage.setItem('username', username);
                    alert('login sukses, login tersimpan');
                    NativeModules.DevSettings.reload()

                  } catch (e) {
                  // saving error
                  }
                }
                else
                {
                   alert('username atau paassword salah')
                }
              }


        render() {
        return (
          <Card>
            <Card.Title>Silakan Login</Card.Title>
            <Card.Divider/>

            <View style={styles.viewRow}>
                <Text>Username </Text>
                <TextInput style={styles.input}
                onChangeText={(username) => this.setState({username})}
                />
            </View>
            <View style={styles.viewRow}>
                <Text>Password </Text>
                <TextInput secureTextEntry={true} style={styles.input}
                onChangeText={(password) => this.setState({password})}
                 />
            </View>
            <View style={styles.viewRow}>
            <Button style={styles.button} title="Submit"
            onPress={()=>
                {
                  this.doLogin(this.state.username, this.state.password)}
                } 

             />
            </View>


          </Card>
        );
        }
       }

       export default Login;



       const styles = StyleSheet.create({
              input: {
                height: 40,
                width:200,
                borderWidth: 1,
                padding: 10,
              },
              button: {
                 height: 40,
                 width:200,  
               },
              viewRow:{
                 flexDirection:"row",
                 justifyContent:"flex-end",
                 alignItems: 'center',
                 paddingRight:50,
                 margin:3
              }
           })
