import { View, ScrollView, TextInput } from "react-native";
import { Text, Button, Card, Icon, Image } from '@rneui/base';
import React, { Component } from "react";
import style from "../assets/style";
import Ionicons from 'react-native-vector-icons/Ionicons';
import { FAB } from 'react-native-paper';
import { useNavigation } from '@react-navigation/native';
import { FlatList } from "react-native-gesture-handler";

class Detail extends React.Component {
    constructor() {
        super();
        this.state = {
            tes: "Menunggu API",
            data: [],
            username: global.activeuser

        }
        this.fetchData();
    }

    dateformatter(d) {
        var date = new Date(d);
        var formateddate = date.toLocaleString("en-GB", { day: '2-digit', month: 'long', year: 'numeric', hour: '2-digit', minute: '2-digit', hour12: false });
        return formateddate;
    }
    fetchData = () => {
        const options = {
            method: 'POST',
            headers: new Headers({
                'Content-Type': 'application/x-www-form-urlencoded'
            }),
            body: "username=" + this.state.username
        };
        try {
            fetch('https://ubaya.fun/react/160819001/mycreation.php', options)
                .then(response => response.json())
                .then(resjson => {
                    this.setState(
                        this.state = {
                            tes: resjson.result,
                            //   tes:resjson.data[0].url,
                            data: resjson.data
                        })
                });
        } catch (error) {
            console.log(error);
        }
    }
    showdata(data) {
        return <FlatList
            data={data}
            keyExtractor={(item) => item.id.toString()}
            renderItem={({ item }) =>
            (
                <Card containerStyle={style.card}>
                    <View style={{
                        position: 'relative'
                    }}>
                        <Text style={style.text_atas}>{item.teksatas}</Text>
                        <Image
                            containerStyle={{
                                aspectRatio: 1,
                            }}
                            source={{
                                uri:
                                    item.url,
                            }}
                        />
                        <Text style={style.text_bawah}>{item.teksbawah}</Text>
                    </View>
                    <View>
                        <View>
                            <Text style={style.text_body}>{
                                this.dateformatter(item.tglbuat)}</Text>
                        </View>
                        <View style={{
                            marginTop: 5,
                            position: 'relative',
                            flexDirection: 'row'
                        }}>
                            <Ionicons
                                name="heart"
                                size={30}
                                color="white"
                            />
                            <Text style={{
                                alignSelf: 'center',
                                left: 5,
                                fontSize: 16,
                                color: '#fff'
                            }}> {item.numoflike} likes</Text>
                            <Text style={{
                                alignSelf: 'center',
                                fontSize: 16,
                                right: 35,
                                marginLeft: 'auto',
                                color: '#fff'
                            }}>{item.banyakcomment} comments</Text>
                <ScrollView>
                    <Card containerStyle={style.card}>
                        <View style={{
                            position: 'relative',
                            flexDirection: 'row'
                        }}>
                            <Text style={{
                                fontSize: 14,
                                color: '#ffffff',
                                // margin: 10,
                                fontWeight: 'bold'
                            }}>David Kurniawan</Text>
                            <Text style={{
                                alignSelf: 'center',
                                fontSize: 14,
                                right: 0,
                                marginLeft: 'auto',
                                color: '#fff'
                            }}>31 Des 22</Text>
                        </View>
                        <Text style={style.text_body}>Isi komentar di sini.</Text>
                    </Card>
                </ScrollView>
                <View style={{
                    position: 'relative',
                    flexDirection: 'row'
                }}>
                    <TextInput
                        style={{
                            borderColor: '#fff',
                            height: 40,
                            borderWidth: 1,
                            padding: 10,
                            backgroundColor: '#fff',
                            borderRadius: 5,
                            width:'90%'
                        }}
                        // onChangeText={(Overview) => this.setState({ Overview })}
                        // value={this.state.Overview}
                        placeholder="Write Comments"
                        multiline={true} numberOfLines={4} />
                    <Button
                        icon={
                            <Ionicons
                                name="send"
                                size={25}
                                color="#917f54"
                            />
                        }
                        buttonStyle={{
                            backgroundColor: 'rgba(0,0,0,0)'
                        }}
                        containerStyle={{
                            position: 'absolute',
                            right: 0
                        }}
                    />
                </View>
                        </View>
                    </View>
                </Card>
            )}
        />
    }
    render() {
        return (
            <View style={style.container}>
                {/* {this.showdata(this.state.data)} */}
                <ScrollView style={style.container}>
                <Card containerStyle={style.card}>
                    
                    <View style={{
                        position: 'relative'
                    }}>
                        <Text style={style.text_atas}>Teks ATAS</Text>
                        <Image
                            containerStyle={{
                                aspectRatio: 1,
                            }}
                            source={{
                                uri:
                                    "https://picsum.photos/500",
                            }}
                        />
                        <Text style={style.text_bawah}>Teks BAWAH</Text>
                    </View>
                    <View style={{
                        marginTop: 5,
                        position: 'relative',
                        flexDirection: 'row'
                    }}>
                        <Ionicons
                            name="heart"
                            size={30}
                            color="white"
                        />
                        <Text style={{
                            alignSelf: 'center',
                            left: 5,
                            fontSize: 16,
                            color: '#fff'
                        }}> 100 likes</Text>
                    </View>
                </Card>
                <Text style={style.text_score}>Comments</Text>

                </ScrollView>

            </View>
        )
    }
}

export default Detail
